import React, { useState } from 'react';

const ResumeUpload = () => {
  const [resume, setResume] = useState(null);
  const [message, setMessage] = useState('');

  const handleFileChange = (e) => {
    setResume(e.target.files[0]);
  };

  const handleUpload = async () => {
    if (!resume) {
      setMessage('Please upload a resume.');
      return;
    }

    const formData = new FormData();
    formData.append('resume', resume);

    try {
      const response = await fetch('http://localhost:5000/api/student/upload-resume', {
        method: 'POST',
        headers: {
          Authorization: `Bearer ${localStorage.getItem('token')}`, // ensure token is stored
        },
        body: formData,
      });

      const data = await response.json();

      if (response.ok) {
        setMessage(data.message);
      } else {
        setMessage(data.message || 'Upload failed');
      }
    } catch (error) {
      console.error(error);
      setMessage('Server error during upload');
    }
  };

  return (
    <div className="p-4 max-w-lg mx-auto">
      <h2 className="text-2xl font-semibold mb-4">Upload Resume</h2>

      {message && <p className="text-green-600 mb-4">{message}</p>}

      <input
        type="file"
        onChange={handleFileChange}
        className="mb-4 p-2 w-full border border-gray-300 rounded"
      />
      <button
        onClick={handleUpload}
        className="bg-blue-600 text-white p-2 rounded w-full"
      >
        Upload Resume
      </button>
    </div>
  );
};

export default ResumeUpload;
