import React, { useEffect, useState } from 'react';

const BrowseResumes = () => {
  const [resumes, setResumes] = useState([]);

  useEffect(() => {
    const fetchResumes = async () => {
      try {
        const res = await fetch('http://localhost:5000/api/company/browse-resumes', {
          headers: {
            Authorization: `Bearer ${localStorage.getItem('token')}`,
          },
        });
        const data = await res.json();
        if (res.ok) setResumes(data.resumes);
      } catch (err) {
        console.error(err);
      }
    };

    fetchResumes();
  }, []);

  const handleShortlist = async (resumeId) => {
    try {
      const res = await fetch(`http://localhost:5000/api/company/shortlist/${resumeId}`, {
        method: 'POST',
        headers: {
          Authorization: `Bearer ${localStorage.getItem('token')}`,
        },
      });

      const data = await res.json();
      if (res.ok) {
        alert(data.message);
        setResumes((prev) =>
          prev.map((r) =>
            r._id === resumeId ? { ...r, shortlisted: true } : r
          )
        );
      } else {
        alert(data.message);
      }
    } catch (err) {
      console.error(err);
    }
  };

  return (
    <div className="p-4">
      <h2 className="text-xl font-semibold mb-4">Browse Resumes</h2>
      {resumes.length === 0 ? (
        <p>No resumes available.</p>
      ) : (
        resumes.map((r) => (
          <div key={r._id} className="border p-2 mb-2 rounded">
            <p><strong>Name:</strong> {r.student.name}</p>
            <p><strong>Email:</strong> {r.student.email}</p>
            <a
              href={`http://localhost:5000${r.resumeUrl}`}
              target="_blank"
              rel="noopener noreferrer"
              className="text-blue-600 underline"
            >
              Download Resume
            </a>
            <button
              onClick={() => handleShortlist(r._id)}
              disabled={r.shortlisted}
              className={`mt-2 block px-4 py-1 rounded ${
                r.shortlisted ? 'bg-gray-400' : 'bg-green-600 text-white'
              }`}
            >
              {r.shortlisted ? 'Shortlisted' : 'Shortlist'}
            </button>
          </div>
        ))
      )}
    </div>
  );
};

export default BrowseResumes;
