import { BookOpen } from 'lucide-react'; // Using lucide-react icons (or use any icon lib)
import React, { useEffect, useState } from 'react';

const ResourceLinks = () => {
  const [resources, setResources] = useState([]);

  useEffect(() => {
    // Simulate fetching resource links
    setResources([
      { title: 'Java Programming', url: 'https://www.w3schools.com/java/', desc: 'Learn Java basics and OOP concepts' },
      { title: 'Python Tutorials', url: 'https://www.learnpython.org', desc: 'Interactive Python learning' },
      { title: 'Aptitude Practice', url: 'https://www.aptitude.com', desc: 'Sharpen your aptitude skills' },
      { title: 'JavaScript Reference', url: 'https://riptutorial.com/javascript', desc: 'Code snippets for JS topics' },
    ]);
  }, []);

  return (
    <div className="p-6 max-w-3xl mx-auto">
      <h2 className="text-3xl font-bold mb-6 text-center text-gray-800">ðŸ“š Recommended Resource Links</h2>

      <div className="grid gap-4">
        {resources.map((resource, index) => (
          <div key={index} className="bg-white shadow-md rounded-xl p-5 border border-gray-200 hover:shadow-lg transition">
            <div className="flex items-start gap-3">
              <BookOpen className="text-blue-500 mt-1" />
              <div>
                <a
                  href={resource.url}
                  className="text-xl font-semibold text-blue-700 hover:underline"
                  target="_blank"
                  rel="noopener noreferrer"
                >
                  {resource.title}
                </a>
                <p className="text-gray-600 text-sm mt-1">{resource.desc}</p>
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default ResourceLinks;
