import axios from 'axios';
import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import collegeLogo from './college-logo.png'; // Ensure you have a logo file

const Login = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const navigate = useNavigate();

  const handleLogin = async (e) => {
    e.preventDefault();

    if (!email || !password) {
      setError('Please fill in all fields.');
      return;
    }

    try {
      const response = await axios.post('http://localhost:5000/api/auth/login', {
        email,
        password,
      });

      localStorage.setItem('token', response.data.token);
      localStorage.setItem('role', response.data.role);

      if (response.data.role === 'student') navigate('/student');
      else if (response.data.role === 'admin') navigate('/admin');
      else if (response.data.role === 'company') navigate('/company');
    } catch (err) {
      setError('Login failed. Please check your credentials.');
    }
  };

  return (
    <div className="login-container">
      <div className="login-box">
        <img src={collegeLogo} alt="Vaagdevi College Logo" className="college-logo" />
        <h2>Vaagdevi College</h2>
        <h3>Login</h3>
        {error && <p className="error">{error}</p>}
        <form onSubmit={handleLogin}>
          <input type="email" value={email} onChange={(e) => setEmail(e.target.value)} placeholder="Email" />
          <input type="password" value={password} onChange={(e) => setPassword(e.target.value)} placeholder="Password" />
          <div className="remember-me">
            <input type="checkbox" id="remember" />
            <label htmlFor="remember">Remember me?</label>
          </div>
          <button type="submit">Login</button>
        </form>
        <p className="forgot-password">Forgot Password?</p>
        <div className="social-login">
          <p>OR</p>
          <div className="social-icons">
            <i className="fab fa-google"></i>
            <i className="fab fa-facebook"></i>
            <i className="fab fa-linkedin"></i>
          </div>
        </div>
        <p>Need an account? <a href="/register">SIGN UP</a></p>
      </div>
    </div>
  );
};

export default Login;
