import { BookOpen } from 'lucide-react'; // optional: npm install lucide-react
import React, { useEffect, useState } from 'react';

const ResourceLinks = () => {
  const [resources, setResources] = useState([]);

  useEffect(() => {
    setResources([
      { title: 'Java Programming', url: 'https://www.w3schools.com/java/', desc: 'Learn Java basics and OOP concepts' },
      { title: 'Python Tutorials', url: 'https://www.learnpython.org', desc: 'Interactive Python learning' },
      { title: 'Aptitude Practice', url: 'https://www.aptitude.com', desc: 'Sharpen your aptitude skills' },
      { title: 'JavaScript Reference', url: 'https://riptutorial.com/javascript', desc: 'Code snippets for JS topics' },
    ]);
  }, []);

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-50 via-white to-purple-50 py-10 px-4">
      <div className="max-w-4xl mx-auto">
        <h2 className="text-4xl font-bold mb-8 text-center text-gray-800">
          ðŸ“š Explore Learning Resources
        </h2>

        <div className="grid sm:grid-cols-2 gap-6">
          {resources.map((resource, index) => (
            <div
              key={index}
              className="group bg-white border border-gray-200 rounded-2xl shadow-sm hover:shadow-lg transition duration-300 p-5 hover:scale-[1.02]"
            >
              <div className="flex items-start gap-4">
                <div className="bg-blue-100 text-blue-600 rounded-full p-2">
                  <BookOpen className="w-6 h-6" />
                </div>
                <div>
                  <a
                    href={resource.url}
                    target="_blank"
                    rel="noopener noreferrer"
                    className="text-lg font-semibold text-blue-700 group-hover:underline"
                  >
                    {resource.title}
                  </a>
                  <p className="text-sm text-gray-600 mt-1">{resource.desc}</p>
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default ResourceLinks;
