import axios from 'axios';
import React, { useEffect, useState } from 'react';

const BrowseResumes = () => {
  const [resumes, setResumes] = useState([]);
  const [loading, setLoading] = useState(true);

  const fetchResumes = async () => {
    try {
      const token = localStorage.getItem('token');
      const res = await axios.get('/api/company/resumes', {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });
      setResumes(res.data);
    } catch (error) {
      console.error('Error fetching resumes:', error.response?.data || error.message);
    } finally {
      setLoading(false);
    }
  };

  const handleShortlist = async (resumeId) => {
    try {
      const token = localStorage.getItem('token');
      const res = await axios.post(
        `/api/company/shortlist/${resumeId}`,
        {},
        {
          headers: {
            'Authorization': `Bearer ${token}`
          }
        }
      );

      setResumes(prev => 
        prev.map(r => 
          r._id === resumeId ? { ...r, shortlisted: true } : r
        )
      );
    } catch (error) {
      console.error('Error shortlisting:', error.response?.data || error.message);
    }
  };

  useEffect(() => {
    fetchResumes();
  }, []);

  if (loading) return <div>Loading resumes...</div>;

  return (
    <div className="p-6">
      <h2 className="text-2xl font-semibold mb-4">Available Resumes</h2>

      {resumes.length === 0 ? (
        <p className="text-gray-500">No resumes available at the moment.</p>
      ) : (
        <div className="space-y-4">
          {resumes.map(resume => (
            <div key={resume._id} className="p-4 border rounded-lg shadow-sm bg-white">
              <div className="flex justify-between items-start">
                <div>
                  <h3 className="font-medium text-lg">{resume.student.name}</h3>
                  <p className="text-gray-600">{resume.student.email}</p>
                  <p className="text-sm text-gray-500">Branch: {resume.student.branch}</p>
                  <a 
                    href={resume.url} 
                    target="_blank" 
                    rel="noopener noreferrer"
                    className="text-blue-600 hover:underline mt-2 inline-block"
                  >
                    View Resume
                  </a>
                </div>

                <button
                  onClick={() => handleShortlist(resume._id)}
                  disabled={resume.shortlisted}
                  className={`px-4 py-2 rounded-md text-sm font-medium ${
                    resume.shortlisted
                      ? 'bg-gray-200 text-gray-600 cursor-not-allowed'
                      : 'bg-green-600 text-white hover:bg-green-700'
                  }`}
                >
                  {resume.shortlisted ? 'Shortlisted' : 'Shortlist'}
                </button>
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default BrowseResumes;
