// components/ViewResumes.js
import React, { useEffect, useState } from 'react';

const ViewResumes = () => {
  const [resumes, setResumes] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchResumes = async () => {
      try {
        const res = await fetch('http://localhost:5000/api/resumes/all', {
          headers: {
            Authorization: `Bearer ${localStorage.getItem('token')}`,
          },
        });

        const data = await res.json();

        if (Array.isArray(data)) {
          setResumes(data);
        } else {
          setResumes([]);
        }

      } catch (err) {
        console.error('Error fetching resumes:', err);
        setResumes([]);
      } finally {
        setLoading(false);
      }
    };

    fetchResumes();
  }, []);

  if (loading) return <p className="p-4">Loading...</p>;

  return (
    <div className="p-6 max-w-5xl mx-auto">
      <h2 className="text-2xl font-bold mb-6">All Uploaded Resumes</h2>
      {resumes.length === 0 ? (
        <p>No resumes uploaded yet.</p>
      ) : (
        <ul className="space-y-4">
          {resumes.map((resume) => (
            <li key={resume._id} className="p-4 border rounded-lg shadow-md bg-white">
              <p><strong>Name:</strong> {resume.student?.name || 'N/A'}</p>
              <p><strong>Email:</strong> {resume.student?.email || 'N/A'}</p>
              <a
                href={`http://localhost:5000${resume.url}`}
                target="_blank"
                rel="noopener noreferrer"
                className="text-blue-600 underline mt-2 inline-block"
              >
                View Resume
              </a>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default ViewResumes;
