import React from 'react';
import { Link } from 'react-router-dom';
import background from '../public/assets/image.jpg'; // Make sure to place the image inside src/assets/
import '../styles/global.css';


const Home = () => {
  return (
    <div className="home-page" style={{ backgroundImage: `url(${background})` }}>
      {/* Top Navigation */}
      <div className="top-nav">
        <Link to="/register" className="top-link">Registration</Link> |
        <Link to="/login" className="top-link">Login</Link>
      </div>

      {/* College Name & Logo */}
      <div className="college-header">
        <img src="/logo.png" alt="Vaagdevi Colleges Logo" className="college-logo" />
        <h2 className="college-name">Vaagdevi Colleges</h2>
      </div>

      {/* Main Title & Tagline */}
      <h1 className="home-title">Hiresmart - Your Gateway to Smarter Hiring & Career Growth ðŸš€</h1>
      <p className="home-subtitle">"Bridging Talent & Opportunities with Smarter Hiring Solutions!"</p>

      {/* Authentication Buttons */}
      <div className="auth-buttons">
        <Link to="/register" className="btn register-btn">Register</Link>
        <Link to="/login" className="btn login-btn">Login</Link>
      </div>
    </div>
  );
};

export default Home;
