import axios from 'axios';
import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import logo from '/mnt/data/image.png'; // Ensure this is the correct path

const Login = () => {
  const navigate = useNavigate();
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');

  const handleLogin = async (e) => {
    e.preventDefault();

    if (!email || !password) {
      setError('Please fill in all fields.');
      return;
    }

    try {
      const response = await axios.post('http://localhost:5000/api/auth/login', { email, password });
      localStorage.setItem('token', response.data.token);
      localStorage.setItem('role', response.data.role);
      navigate(`/${response.data.role}`);
    } catch (err) {
      setError('Login failed. Please check your credentials.');
      console.error(err);
    }
  };

  return (
    <div className="flex justify-center items-center h-screen bg-gradient-to-r from-blue-100 to-blue-300">
      <div className="bg-white p-8 rounded-lg shadow-lg w-96 text-center">
        <img src={logo} alt="Vaagdevi Colleges" className="w-24 mx-auto mb-4" />
        <h2 className="text-2xl font-semibold mb-4">Login</h2>
        {error && <p className="text-red-600 mb-4">{error}</p>}
        <form onSubmit={handleLogin}>
          <input type="email" value={email} onChange={(e) => setEmail(e.target.value)} placeholder="Email" className="mb-4 p-2 w-full border border-gray-300 rounded" />
          <input type="password" value={password} onChange={(e) => setPassword(e.target.value)} placeholder="Password" className="mb-4 p-2 w-full border border-gray-300 rounded" />
          <button type="submit" className="bg-blue-600 text-white p-2 rounded w-full">Login</button>
        </form>
        <p className="mt-4">Don't have an account? <a href="/register" className="text-blue-600">Register here</a></p>
      </div>
    </div>
  );
};

export default Login;
