import React, { useEffect, useState } from 'react';
import { getJobs, postJob } from '../../services/api';

const ManageJobs = () => {
  const [jobs, setJobs] = useState([]);
  const [newJob, setNewJob] = useState({ company: '', title: '', eligibility: '', deadline: '' });

  useEffect(() => {
    const fetchJobs = async () => {
      const jobsList = await getJobs();
      setJobs(jobsList);
    };
    fetchJobs();
  }, []);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setNewJob((prevJob) => ({ ...prevJob, [name]: value }));
  };

  const handlePostJob = async () => {
    try {
      await postJob(newJob);
      alert('Job posted successfully!');
    } catch (err) {
      alert('Failed to post job');
    }
  };

  return (
    <div className="manage-jobs">
      <h2>Manage Job Listings</h2>
      <div>
        <h3>Post New Job</h3>
        <input
          type="text"
          name="company"
          value={newJob.company}
          onChange={handleInputChange}
          placeholder="Company"
        />
        <input
          type="text"
          name="title"
          value={newJob.title}
          onChange={handleInputChange}
          placeholder="Job Title"
        />
        <input
          type="text"
          name="eligibility"
          value={newJob.eligibility}
          onChange={handleInputChange}
          placeholder="Eligibility"
        />
        <input
          type="date"
          name="deadline"
          value={newJob.deadline}
          onChange={handleInputChange}
        />
        <button onClick={handlePostJob}>Post Job</button>
      </div>
      <h3>Current Job Listings</h3>
      <ul>
        {jobs.map((job) => (
          <li key={job._id}>{job.title} at {job.company}</li>
        ))}
      </ul>
    </div>
  );
};

export default ManageJobs;
