import axios from 'axios';
import React, { useEffect, useState } from 'react';
import '../../styles/ViewResumes.css';

const BrowseResumes = () => {
  const [resumes, setResumes] = useState([]);
  const [loading, setLoading] = useState(true);

  const fetchResumes = async () => {
    try {
      const token = localStorage.getItem('token');
      const res = await axios.get('/api/company/resumes', {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });
      setResumes(res.data);
    } catch (error) {
      console.error('Error fetching resumes:', error.response?.data || error.message);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchResumes();
  }, []);

  if (loading) return <div className="loading-text">Loading resumes...</div>;

  return (
    <div className="browse-resumes-container">
      <h2 className="title">Available Resumes</h2>

      {resumes.length === 0 ? (
        <p className="empty-message">No resumes available at the moment.</p>
      ) : (
        <ul className="resume-list">
          {resumes.map((resume) => (
            <li key={resume._id} className="resume-card">
              <div className="resume-info">
                <h3>{resume.student?.name || 'N/A'}</h3>
                <p>{resume.student?.email || 'N/A'}</p>
                <p>Branch: {resume.student?.branch || 'N/A'}</p>
                <a
                  href={resume.url.startsWith('http') ? resume.url : `http://localhost:5000${resume.url}`}
                  target="_blank"
                  rel="noopener noreferrer"
                  className="view-button"
                >
                  View Resume
                </a>
              </div>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default BrowseResumes;
