const Register = () => {
  const navigate = useNavigate();
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [role, setRole] = useState('student');
  const [error, setError] = useState('');

  const handleRegister = async (e) => {
    e.preventDefault();

    if (!name || !email || !password) {
      setError('Please fill in all fields.');
      return;
    }

    try {
      const response = await axios.post('http://localhost:5000/api/auth/register', { name, email, password, role });
      localStorage.setItem('token', response.data.token);
      localStorage.setItem('role', role);
      navigate(`/${role}`);
    } catch (err) {
      setError('Registration failed. Please try again.');
      console.error(err);
    }
  };

  return (
    <div className="flex justify-center items-center h-screen bg-gradient-to-r from-blue-100 to-blue-300">
      <div className="bg-white p-8 rounded-lg shadow-lg w-96 text-center">
        <img src={logo} alt="Vaagdevi Colleges" className="w-24 mx-auto mb-4" />
        <h2 className="text-2xl font-semibold mb-4">Register</h2>
        {error && <p className="text-red-600 mb-4">{error}</p>}
        <form onSubmit={handleRegister}>
          <input type="text" value={name} onChange={(e) => setName(e.target.value)} placeholder="Name" className="mb-4 p-2 w-full border border-gray-300 rounded" />
          <input type="email" value={email} onChange={(e) => setEmail(e.target.value)} placeholder="Email" className="mb-4 p-2 w-full border border-gray-300 rounded" />
          <input type="password" value={password} onChange={(e) => setPassword(e.target.value)} placeholder="Password" className="mb-4 p-2 w-full border border-gray-300 rounded" />
          <select value={role} onChange={(e) => setRole(e.target.value)} className="mb-4 p-2 w-full border border-gray-300 rounded">
            <option value="student">Student</option>
            <option value="admin">Admin</option>
            <option value="company">Company</option>
          </select>
          <button type="submit" className="bg-blue-600 text-white p-2 rounded w-full">Register</button>
        </form>
        <p className="mt-4">Already have an account? <a href="/login" className="text-blue-600">Login here</a></p>
      </div>
    </div>
  );
};

export default Register;
