import React, { useEffect, useState } from 'react';

const BrowseResumes = () => {
  const [resumes, setResumes] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchResumes = async () => {
      try {
        const res = await fetch('http://localhost:5000/api/company/browse-resumes', {
          headers: {
            Authorization: `Bearer ${localStorage.getItem('token')}`,
          },
        });

        const data = await res.json();
        if (res.ok) {
          setResumes(data.resumes);
        } else {
          console.error(data.message || 'Failed to fetch resumes');
        }
      } catch (err) {
        console.error('Fetch error:', err.message);
      } finally {
        setLoading(false);
      }
    };

    fetchResumes();
  }, []);

  const handleShortlist = async (resumeId) => {
    try {
      const res = await fetch(`http://localhost:5000/api/company/shortlist/${resumeId}`, {
        method: 'POST',
        headers: {
          Authorization: `Bearer ${localStorage.getItem('token')}`,
        },
      });

      const data = await res.json();
      if (res.ok) {
        alert(data.message);
        setResumes((prevResumes) =>
          prevResumes.map((r) =>
            r._id === resumeId ? { ...r, shortlisted: true } : r
          )
        );
      } else {
        alert(data.message);
      }
    } catch (err) {
      console.error('Shortlist error:', err.message);
    }
  };

  if (loading) return <p className="p-4">Loading resumes...</p>;

  return (
    <div className="p-6">
      <h2 className="text-2xl font-bold mb-4">Student Resumes</h2>
      {resumes.length === 0 ? (
        <p>No resumes found.</p>
      ) : (
        <div className="grid gap-4">
          {resumes.map((r) => (
            <div key={r._id} className="border p-4 rounded-lg shadow">
              <p><strong>Name:</strong> {r.student?.name || 'N/A'}</p>
              <p><strong>Email:</strong> {r.student?.email || 'N/A'}</p>

              <a
                href={`http://localhost:5000${r.resumeUrl}`}
                target="_blank"
                rel="noopener noreferrer"
                className="inline-block mt-2 text-blue-600 hover:underline"
              >
                ðŸ“„ View Resume
              </a>

              <button
                onClick={() => handleShortlist(r._id)}
                disabled={r.shortlisted}
                className={`mt-3 block w-fit px-4 py-2 rounded ${
                  r.shortlisted
                    ? 'bg-gray-400 cursor-not-allowed text-white'
                    : 'bg-green-600 hover:bg-green-700 text-white'
                }`}
              >
                {r.shortlisted ? 'Shortlisted' : 'Shortlist'}
              </button>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default BrowseResumes;
