import React from 'react';
import { Link } from 'react-router-dom';
import '../styles/global.css'; // Ensure styles are imported

const Home = () => {
  return (
    <div className="home-container">
      {/* Title & Tagline */}
      <header className="header-section">
        <h1 className="main-title">Hiresmart - Your Gateway to Smarter Hiring & Career Growth ðŸš€</h1>
        <p className="tagline">Bridging Talent & Opportunities with Smarter Hiring Solutions!</p>
      </header>

      {/* Main Content Section */}
      <div className="content">
        {/* Left Side: Login & Register */}
        <div className="left-section">
          {/* College Name & Logo */}
          <div className="college-header">
            <img src="/logo.png" alt="Vaagdevi Colleges Logo" className="college-logo" />
            <h2 className="college-name">Vaagdevi Colleges</h2>
          </div>

          {/* Authentication Buttons */}
          <div className="auth-buttons">
            <Link to="/register" className="btn register-btn">Register</Link>
            <Link to="/login" className="btn login-btn">Login</Link>
          </div>

          {/* Tagline Below Buttons */}
          <p className="bottom-tagline">Bridging Talent & Opportunities with Smarter Hiring Solutions!</p>
        </div>

        {/* Right Side: Image */}
        <div className="right-section">
          <img src="/image.jpg" alt="Professional Workspace" className="illustration" />
        </div>
      </div>
    </div>
  );
};

export default Home;
