import axios from 'axios';
import React, { useEffect, useState } from 'react';

const BrowseResumes = () => {
  const [resumes, setResumes] = useState([]);

  const fetchResumes = async () => {
    try {
      const res = await axios.get('/api/resumes');
      setResumes(res.data);
    } catch (error) {
      console.error('Error fetching resumes:', error);
    }
  };

  const handleShortlist = async (resumeId) => {
    try {
      const res = await axios.post(`/api/resumes/shortlist/${resumeId}`);
      const updatedResume = res.data.resume;

      setResumes((prev) =>
        prev.map((r) => (r._id === resumeId ? updatedResume : r))
      );
    } catch (error) {
      console.error('Error shortlisting candidate:', error);
    }
  };

  useEffect(() => {
    fetchResumes();
  }, []);

  return (
    <div className="p-6">
      <h2 className="text-2xl font-semibold mb-4">All Student Resumes</h2>
      {resumes.length === 0 ? (
        <p>No resumes found.</p>
      ) : (
        <div className="grid gap-4">
          {resumes.map((r) => (
            <div
              key={r._id}
              className="p-4 border rounded shadow bg-white flex justify-between items-center"
            >
              <div>
                <p className="text-lg font-medium">{r.student.name}</p>
                <p className="text-sm text-gray-500">{r.student.email}</p>
                <p className="text-sm text-gray-600">Branch: {r.student.branch}</p>
                <a
                  href={`/${r.resumeUrl}`}
                  target="_blank"
                  rel="noopener noreferrer"
                  className="text-blue-600 underline"
                >
                  View Resume
                </a>
              </div>
              <button
                onClick={() => handleShortlist(r._id)}
                disabled={r.shortlisted}
                className={`px-4 py-2 rounded ${
                  r.shortlisted
                    ? 'bg-gray-400 cursor-not-allowed text-white'
                    : 'bg-green-600 hover:bg-green-700 text-white'
                }`}
              >
                {r.shortlisted ? 'Shortlisted' : 'Shortlist'}
              </button>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default BrowseResumes;
