import React, { useEffect, useState } from 'react';

const ViewResumes = () => {
  const [resumes, setResumes] = useState([]);

  useEffect(() => {
    const fetchResumes = async () => {
      try {
        const res = await fetch('http://localhost:5000/api/student/resumes', {
          headers: {
            Authorization: `Bearer ${localStorage.getItem('token')}`,
          },
        });
        const data = await res.json();
        if (res.ok) setResumes(data.resumes);
      } catch (err) {
        console.error(err);
      }
    };

    fetchResumes();
  }, []);

  return (
    <div className="p-4">
      <h2 className="text-xl font-semibold mb-4">View Student Resumes</h2>
      {resumes.length === 0 ? (
        <p>No resumes uploaded yet.</p>
      ) : (
        resumes.map((r) => (
          <div key={r._id} className="border p-2 mb-2 rounded">
            <p><strong>Name:</strong> {r.student.name}</p>
            <p><strong>Email:</strong> {r.student.email}</p>
            <a
              href={`http://localhost:5000${r.resumeUrl}`}
              target="_blank"
              rel="noopener noreferrer"
              className="text-blue-600 underline"
            >
              Download Resume
            </a>
          </div>
        ))
      )}
    </div>
  );
};

export default ViewResumes;
