import React, { useEffect, useState } from 'react';
import '../../styles/ViewResumes.js';

const ViewResumes = () => {
  const [resumes, setResumes] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchResumes = async () => {
      try {
        const res = await fetch('http://localhost:5000/api/resumes/all', {
          headers: {
            Authorization: `Bearer ${localStorage.getItem('token')}`,
          },
        });

        const data = await res.json();

        if (Array.isArray(data.resumes)) {
          setResumes(data.resumes);
        } else {
          setResumes([]);
        }
      } catch (err) {
        console.error('Error fetching resumes:', err);
        setResumes([]);
      } finally {
        setLoading(false);
      }
    };

    fetchResumes();
  }, []);

  if (loading) return <div className="loading">Loading resumes...</div>;

  return (
    <div className="view-resumes-container">
      <h2 className="title">Uploaded Resumes</h2>

      {resumes.length === 0 ? (
        <p className="no-resumes">No resumes uploaded yet.</p>
      ) : (
        <ul className="resume-list">
          {resumes.map((resume) => (
            <li key={resume.id} className="resume-card">
              <p><strong>Name:</strong> {resume.student?.name || 'N/A'}</p>
              <p><strong>Email:</strong> {resume.student?.email || 'N/A'}</p>
              <p><strong>Branch:</strong> {resume.student?.branch || 'N/A'}</p>
              <p><strong>CGPA:</strong> {resume.student?.cgpa || 'N/A'}</p>
              <a
                href={`http://localhost:5000${resume.downloadUrl}`}
                target="_blank"
                rel="noopener noreferrer"
                className="view-button"
              >
                View Resume
              </a>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default ViewResumes;
